import React, {Component} from 'react';
import {useState} from 'react';
import axios from 'axios';
import InputGroup from 'react-bootstrap/InputGroup'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import FileUpload from './fileupload';
import {AssessmentRounded} from '@material-ui/icons';
import ApplicantNav from './applicantnavbar'
import Rating from '@material-ui/lab/Rating'
const rest_form=[];
let educatin_code=<div></div>;


export default class ApplicantProfile extends Component {

    constructor(props) {
        super(props);

        this.state = {
            email: '',
            name: '',
            password: '',
            conf_pass: '',
            institute: [''],
            start_year: [''],
            end_year: [''],
            skills: [''],
            resumee: null,
            file: null,
            filename: '',
            filepath: '',
            progress: 0,
            profile_pic: null,
            profile: null,
            profilename: '',
            profilepath: '',
            profileprogress: 0,
            ratingsum: 0,
            ratingcount: 0
        }
        this.onChangeUser = this.onChangeUser.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onCompletion = this.onCompletion.bind(this);
        this.onChangeName = this.onChangeName.bind(this);
        // this.onChangeContact = this.onChangeContact.bind(this);
        // this.onChangeBio = this.onChangeBio.bind(this);
        this.onChangePass = this.onChangePass.bind(this);
        this.onChangeconf = this.onChangeconf.bind(this);
